import React, { useState, useEffect } from "react";
import { useParams, useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import CKEditor from "ckeditor4-react";

const Editor = () => {
  const { id } = useParams();
  const location = useLocation();
  const documentData = location.state?.documentData;
  const [document, setDocument] = useState(documentData || {});
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDocument = async (documentId) => {
      const token = localStorage.getItem("auth_token");
      if (!token) {
        console.error("No token found in local storage");
        return;
      }

      try {
        const response = await fetch(`http://192.168.0.114:8000/api/documents/${documentId}/`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Token ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error("Failed to fetch document data");
        }

        const data = await response.json();
        setDocument(data);
      } catch (error) {
        console.error("An error occurred while fetching the document:", error);
        setError("Failed to fetch document. Please try again later.");
      }
    };

    if (id && !documentData) {
      fetchDocument(id);
    }
  }, [id, documentData]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center">
      {error && <div className="text-red-500 text-lg">{error}</div>}
      <motion.div
        initial={{ opacity: 0, x: -50 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-4xl bg-white shadow-lg rounded-lg p-6"
      >
        <h1 className="text-3xl font-bold mb-4">Document Editor</h1>
        <CKEditor
          data={document.content || ""}
          onChange={(event) => {
            const data = event.editor.getData();
            setDocument((prevDocument) => ({
              ...prevDocument,
              content: data,
            }));
          }}
        />
        {/* Additional form fields to edit other parts of the document can go here */}
      </motion.div>
    </div>
  );
};

export default Editor;
