// Importing helper modules
import { useCallback, useMemo, useRef, useState } from "react";
import ReactQuill from "react-quill";
import { marked } from "marked"; // Import the 'marked' library for Markdown conversion

// Importing styles
import "react-quill/dist/quill.snow.css";
import styles from "./styles.modules.css";

const Editor = () => {
  // Editor state
  const [editorValue, setEditorValue] = useState("");
  const [promptValue, setPromptValue] = useState("");

  // Editor ref
  const quill = useRef();

  // Sample markdown data with various markdown syntaxes
  const markdownData = `
  # Sample Form
  # Section 1
  **Question 1:** Describe your experience with React.
  - **Question 2:** How do you manage state in React applications?
  
  **Section 2**
  - **Question 1:** What are your thoughts on server-side rendering?
  - **Question 2:** Explain the difference between CSS-in-JS and traditional CSS.

  ### Table Example
  | Syntax | Description |
  | ----------- | ----------- |
  | **Bold** | This is **bold** text |
  | *Italic* | This is *italic* text |
  | \`Code\` | This is \`inline code\` |
  `;

  // Convert markdown to HTML and set it as initial editor data
  useMemo(() => {
    const htmlData = marked(markdownData);
    setEditorValue(htmlData);
  }, []);

  // Handler to handle button clicked
  function handler() {
    console.log(editorValue);
  }

  const imageHandler = useCallback(() => {
    // Create an input element of type 'file'
    const input = document.createElement("input");
    input.setAttribute("type", "file");
    input.setAttribute("accept", "image/*");
    input.click();

    // When a file is selected
    input.onchange = () => {
      const file = input.files[0];
      const reader = new FileReader();

      // Read the selected file as a data URL
      reader.onload = () => {
        const imageUrl = reader.result;
        const quillEditor = quill.current.getEditor();

        // Get the current selection range and insert the image at that index
        const range = quillEditor.getSelection(true);
        quillEditor.insertEmbed(range.index, "image", imageUrl, "user");
      };

      reader.readAsDataURL(file);
    };
  }, []);

  const modules = useMemo(
    () => ({
      toolbar: {
        container: [
          [{ header: [2, 3, 4, false] }],
          ["bold", "italic", "underline", "blockquote"],
          [{ color: [] }],
          [
            { list: "ordered" },
            { list: "bullet" },
            { indent: "-1" },
            { indent: "+1" },
          ],
          ["link", "image"],
          ["clean"],
        ],
        handlers: {
          image: imageHandler,
        },
      },
      clipboard: {
        matchVisual: true,
      },
    }),
    [imageHandler]
  );

  const formats = [
    "header",
    "bold",
    "italic",
    "underline",
    "strike",
    "blockquote",
    "list",
    "bullet",
    "indent",
    "link",
    "image",
    "color",
    "clean",
  ];

  return (
    <div className={styles.MyEditor}>
      <div className="bg-purple-500 flex justify-center items-center pb-3 pt-3">
        <h1 className="text-3xl font-bold text-white">Forms Editor</h1>
      </div>
      <div className="grid grid-cols-12 gap-6">
        <div className="col-span-8 bg-white p-6 shadow-lg rounded-lg">
          <ReactQuill
            ref={(el) => (quill.current = el)}
            theme="snow"
            value={editorValue}
            formats={formats}
            modules={modules}
            readOnly={true} // Set editor to read-only
          />
        </div>
        <div className="col-span-4 bg-white p-6 shadow-lg rounded-lg">
          <div className="mb-4">
            <div className="bg-purple-500 flex justify-center items-center pb-3 pt-3">
              <h1 className="text-2xl font-bold text-white">User Prompt</h1>
            </div>
            <textarea
              id="prompt"
              value={promptValue}
              onChange={(e) => setPromptValue(e.target.value)}
              rows="10"
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              style={{ borderColor: "transparent" }} // Remove the blue border
              placeholder="Enter your prompt here..."
            ></textarea>
          </div>
          <button
            onClick={handler}
            className="w-full bg-purple-500 text-white py-2 px-4 rounded-lg hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Generate
          </button>
        </div>
      </div>
    </div>
  );
};

export default Editor;
